package task1_practical;

class control_structures {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n=0;
		System.out.println("Sum of first ten natural numbers");
		for(int i=1;i<=10;i++) {
			 n=n+i;
			
			System.out.println("Sum of 1st to 10 natural numbers"+n);
		}
		System.out.println("Total sum of the natural numbers is:"+n);
			}

	}


